document.getElementById("pick_form").addEventListener("submit",e=>{e.preventDefault();const t=document.getElementById("pick_error");t.style.display="none";const n=document.getElementById("pick_select");if(""==n.value)t.innerText="Select a learner and course.",t.style.display="block";else{const e=n.value.split("-");window.location.href=`./admin_user.php?uid=${e[0]}&cid=${e[1]}`}});